// frontend/src/App.js
import React, { useState } from "react";
import axios from "axios"; // Use axios to send API requests

function App() {
  // States for storing URL input, word frequencies, and loading state
  const [url, setUrl] = useState("");
  const [frequencies, setFrequencies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle the form submission and fetch word frequency data from the backend
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission behavior
    setLoading(true); // Set loading state to true
    setError(null); // Reset error message

    try {
      // Make an API request to the backend to fetch word frequencies for the URL
      const response = await axios.post("http://localhost:5000/api/frequencies", { url });
      setFrequencies(response.data); // Set word frequencies from response
    } catch (err) {
      setError("An error occurred while fetching data. Please try again."); // Handle errors
    } finally {
      setLoading(false); // Set loading state back to false
    }
  };

  return (
    <div className="App">
      <h1>Word Frequency Analyser</h1>

      {/* Form to input URL and analyze word frequencies */}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter URL to analyze"
          value={url}
          onChange={(e) => setUrl(e.target.value)} // Update URL state on input change
        />
        <button type="submit" disabled={loading}>Analyze</button>
      </form>

      {/* Display loading state */}
      {loading && <p>Loading...</p>}

      {/* Display error message */}
      {error && <p className="error">{error}</p>}

      {/* Display word frequencies in a table if available */}
      {frequencies.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Word</th>
              <th>Frequency</th>
            </tr>
          </thead>
          <tbody>
            {frequencies.map((item, index) => (
              <tr key={index}>
                <td>{item.word}</td>
                <td>{item.frequency}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
